import React from "react";
import { connect } from "react-redux";
import { addArticle, changeNumberOfPairs } from "../redux/actions/index";

function App(props) {
  return (
    <div>
      <button onClick={props.changeNumberOfPairs}>change</button>
      <div>number of pairs : {props.numberOfPairs}</div>
    </div>
  );
}

const mapStateToProps = (state) => ({
  cards: state.cards,
  numberOfPairs: state.numberOfPairs,
});

const mapDispatchToProps = (dispatch) => ({
  changeNumberOfPairs: () => dispatch(changeNumberOfPairs(21)),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
