import React, { useEffect } from "react";
import { connect } from "react-redux";

function Grid(props) {
  //  mount
  //   useEffect(() => {
  //     console.log("Component did mount");
  //   }, []);

  //   mount + update
  useEffect(() => {
    // shuffle
    // limit to numberOfPairs
    // mirror
    // Shuflle
    console.log(props.numberOfPairs);
  }, [props.numberOfPairs]);

  return (
    //   grid Item
    <>
      {props.cards.map((el, index) => {
        if (el.isActive) {
          return <img key={el.src + index} src={el.src} />;
        }
      })}
    </>
  );
}

const mapStateToProps = (state) => ({
  numberOfPairs: state.numberOfPairs,
  cards: state.cards,
});

export default connect(mapStateToProps)(Grid);
