import React, { useEffect } from "react";
import { connect } from "react-redux";
import styled from "styled-components";
import {
  LIMIT_CARDS,
  SHUFFLE,
  DUPLICATE_CARDS,
  RESET_CARDS,
  FLIP_ALL_CARDS,
  FLIP_CARD,
} from "../redux/constants/action-types";

function Grid(props) {
  useEffect(() => {
    props.resetCards();
    props.shuffleCards();
    props.limitCards();
    props.duplicateCards();
    props.shuffleCards();

    setTimeout(() => {
      props.flipAllCards();
    }, 1500);
  }, [props.numberOfPairs]);

  return (
    //   grid Item
    <gridContainer>
      {props.cards.map((el, index) => {
        if (el.isActive) {
          return (
            <div
              style={{
                margin: "0 1px",
                display: "inline-block",
                height: "125px",
                width: "125px",
                backgroundColor: "#bbb",
              }}
            >
              <img key={el.src + index} data-index={el.index} src={el.src} />
            </div>
          );
        } else {
          return (
            <div
              style={{
                margin: "0 1px",
                display: "inline-block",
                height: "125px",
                width: "125px",
                backgroundColor: "#bbb",
              }}
            >
              <img
                onClick={() => flipCard(el.index)}
                style={{ display: "none" }}
                key={el.src + index}
                data-index={el.index}
                src={el.src}
              />
            </div>
          );
        }
      })}
    </gridContainer>
  );
}

const mapStateToProps = (state) => ({
  numberOfPairs: state.numberOfPairs,
  cards: state.cards,
  gameState: state.gameState,
});

const mapDispatchToProps = (dispatch) => ({
  shuffleCards: () => dispatch({ type: SHUFFLE }),
  limitCards: () => dispatch({ type: LIMIT_CARDS }),
  duplicateCards: () => dispatch({ type: DUPLICATE_CARDS }),
  resetCards: () => dispatch({ type: RESET_CARDS }),
  flipAllCards: () => dispatch({ type: FLIP_ALL_CARDS }),
  flipCard: (index) => dispatch({ type: FLIP_CARD, payload: index }),
});

export default connect(mapStateToProps, mapDispatchToProps)(Grid);

// ---------------------------------
const gridContainer = styled.div`
  width: 520px;
  height: 520px;
  margin: 5px auto;
`;
