import React, { useEffect } from "react";
import { connect } from "react-redux";
import styled from "styled-components";

function Grid(props) {
  //  mount
  //   useEffect(() => {
  //     console.log("Component did mount");
  //   }, []);

  //   mount + update
  useEffect(() => {
    // shuffle
    // limit to numberOfPairs
    // mirror
    // Shuflle
    console.log(props.numberOfPairs);
  }, [props.numberOfPairs]);

  return (
    //   grid Item
    <gridContainer>
      {props.cards.map((el, index) => {
        if (el.isActive) {
          return (
            <img key={el.src + index} width="125" height="125" src={el.src} />
          );
        }
      })}
    </gridContainer>
  );
}

const mapStateToProps = (state) => ({
  numberOfPairs: state.numberOfPairs,
  cards: state.cards,
});

export default connect(mapStateToProps)(Grid);

// ---------------------------------
const gridContainer = styled.div`
  width: 520px;
  height: 520px;
`;
