import {
  CHANGE_NUMBER_OF_PAIRS,
  SHUFFLE,
  LIMIT_CARDS,
  DUPLICATE_CARDS,
  RESET_CARDS,
  START_GAME,
  FLIP_ALL_CARDS,
  FLIP_CARD,
} from "../constants/action-types";
import { HYDRATE } from "next-redux-wrapper";

const initialState = {
  numberOfPairs: 6,
  width: 73,
  gameState: "loading",
  cards: [
    {
      src: "/images/animals-bunny.jpg",
      isActive: false,
    },
    {
      src: "/images/animals-cat.jpg",
      isActive: false,
    },
    {
      src: "/images/animals-cat-2.jpg",
      isActive: false,
    },
    {
      src: "/images/animals-dog.jpg",
      isActive: false,
    },
    {
      src: "/images/animals-dog-2.jpg",
      isActive: false,
    },
    {
      src: "/images/animals-horse.jpg",
      isActive: false,
    },
    {
      src: "/images/animals-horse-2.jpg",
      isActive: false,
    },
    {
      src: "/images/architecture-london-towerbridge.jpg",
      isActive: false,
    },
    {
      src: "/images/architecture-moscow-redsquare.jpg",
      isActive: false,
    },
    {
      src: "/images/architecture-nederlanden.jpg",
      isActive: false,
    },
    {
      src: "/images/architecture-newyork-publiclibrary.jpg",
      isActive: false,
    },
    {
      src: "/images/architecture-paris-eiffeltower.jpg",
      isActive: false,
    },
    {
      src: "/images/cities-tokyo-night.jpg",
      isActive: false,
    },
    {
      src: "/images/flowers-reddahlia.jpg",
      isActive: false,
    },
    {
      src: "/images/flowers-waterlillies.jpg",
      isActive: false,
    },
    {
      src: "/images/flowers-windclock.jpg",
      isActive: false,
    },
    {
      src: "/images/landscape-1.jpg",
      isActive: false,
    },
    {
      src: "/images/landscape-2.jpg",
      isActive: false,
    },
    {
      src: "/images/landscape-2.jpg",
      isActive: false,
    },
    {
      src: "/images/landscape-netherlands-deurningen.jpg",
      isActive: false,
    },
    {
      src: "/images/landscape-us-edgewood.jpg",
      isActive: false,
    },
  ],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case HYDRATE:
      return { ...state, ...action.payload };
      break;
    case CHANGE_NUMBER_OF_PAIRS:
      return Object.assign({}, state, {
        numberOfPairs: action.payload,
      });
      break;
    case START_GAME:
      return Object.assign({}, state, { gameState: "playing" });
      break;
    case RESET_CARDS:
      let initialCards = initialState.cards;
      return Object.assign({}, state, { cards: initialCards });
      break;
    case SHUFFLE:
      let array = state.cards;
      let len = array.length - 1;
      for (let i = len; i > 0; i--) {
        const j = Math.floor(Math.random() * i);
        const temp = array[i];
        array[i] = array[j];
        array[j] = temp;
      }
      return Object.assign({}, state, { cards: [...array] });
      break;
    case LIMIT_CARDS:
      let limitedCards = state.cards;

      limitedCards.splice(state.numberOfPairs);
      limitedCards.forEach((el, index) => {
        el.index = index;
        el.isActive = true;
      });
      return Object.assign({}, state, { cards: [...limitedCards] });
      break;
    case DUPLICATE_CARDS:
      let duplicatedCards = state.cards;
      duplicatedCards = [...duplicatedCards, ...duplicatedCards];
      return Object.assign({}, state, { cards: [...duplicatedCards] });
      break;
    case FLIP_ALL_CARDS:
    // let flipedCards = state.cards;
    // flipedCards.forEach((el) => {
    //   el.isActive = false;
    // });
    // return Object.assign({}, state, { cards: [...flipedCards] });
    case FLIP_CARD:
      let flipedCard = state.cards.find((el) => el.index == action.payload);
      flipedCard.isActive = true;

      return Object.assign({}, state, { cards: [...state.cards, flipedCard] });
    default:
      return state;
  }
}

export default rootReducer;

