import React, { useEffect } from "react";
import { connect } from "react-redux";
import styled from "styled-components";
import {
  LIMIT_CARDS,
  SHUFFLE,
  DUPLICATE_CARDS,
  RESET_CARDS,
} from "../redux/constants/action-types";

function renderCards(gameState) {
  if (gameState === "loading") {
     props.cards.map((el, index) => {
      return <img key={el.src + index} width="125" height="125" src={el.src} />;
    });
  } else if (gameState === "playing") {
    return (props.cards.map((el, index) => {
      return <div>1</div>;
    }));
  }
}
function Grid(props) {
  useEffect(() => {
    props.resetCards();
    props.shuffleCards();
    props.limitCards();
    props.duplicateCards();
  }, [props.numberOfPairs]);

  return (
    //   grid Item
    <gridContainer>{renderCards(props.gameState)}</gridContainer>
  );
}

const mapStateToProps = (state) => ({
  numberOfPairs: state.numberOfPairs,
  cards: state.cards,
  gameState: state.gameState,
});

const mapDispatchToProps = (dispatch) => ({
  shuffleCards: () => dispatch({ type: SHUFFLE }),
  limitCards: () => dispatch({ type: LIMIT_CARDS }),
  duplicateCards: () => dispatch({ type: DUPLICATE_CARDS }),
  resetCards: () => dispatch({ type: RESET_CARDS }),
});

export default connect(mapStateToProps, mapDispatchToProps)(Grid);

// ---------------------------------
const gridContainer = styled.div`
  width: 520px;
  height: 520px;
  margin: 5px auto;
`;
