import React from "react";
import { connect } from "react-redux";
import { addArticle, changeNumberOfPairs } from "../redux/actions/index";
import { Layout } from "antd";
import styled from "styled-components";
import Control from "../components/Control";

const { Content, Sider } = Layout;

function App(props) {
  return (
    <Layout>
      {/* <button onClick={() => props.changeNumberOfPairs(90)}>change</button>
      <div>number of pairs : {props.numberOfPairs}</div>
      <Button type="primary">Button</Button> */}

      <Content>{/* Game Grid */}</Content>
      <Sider>
        <Control />.
      </Sider>
    </Layout>
  );
}

const mapStateToProps = (state) => ({
  cards: state.cards,
  numberOfPairs: state.numberOfPairs,
});

const mapDispatchToProps = (dispatch) => ({
  changeNumberOfPairs: (number) => dispatch(changeNumberOfPairs(number)),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
