import {
  CHANGE_NUMBER_OF_PAIRS,
  SHUFFLE,
  LIMIT_CARDS,
  DUPLICATE_CARDS,
  RESET_CARDS,
  START_GAME,
  FLIP_ALL_CARDS,
  FLIP_CARD,
  CHANGE_WIDTH,
} from "../constants/action-types";
import { HYDRATE } from "next-redux-wrapper";
import { flipCard } from "../actions";

const clone = require("rfdc")();

const initialState = {
  width: 127,
  numberOfPairs: 6,
  numberOfFoundPairs: 0,
  numberOfAttempts: 0,
  isLoading: true,
  isPlaying: false,
  clickCounter: 0,
  matching: false,
  matchingCard: null,
  cards: [
    {
      src: "/images/animals-bunny.jpg",
